package com.tiddev.authorization.config.key;

import lombok.Getter;
import lombok.Setter;
import org.springframework.boot.context.properties.ConfigurationProperties;

@ConfigurationProperties(prefix = "rsa.key")
public class RSAConfig {
    private final static String DEFAULT_PRIVATE_KEY = "";
    private final static String DEFAULT_PUBLIC_KEY = "";
    private final static String DEFAULT_RSA_KID = "180afcbe-0122-4458-b0ae-9610c46c333e";
    @Getter
    @Setter
    private String privateKey = DEFAULT_PRIVATE_KEY;
    @Getter
    @Setter
    private String publicKey = DEFAULT_PUBLIC_KEY;
    @Getter
    @Setter
    private String kid = DEFAULT_RSA_KID;


}
